p='heads';q='tails';$=id=>document.getElementById(id);h=t=0;b=()=>{s=h+t;$(p).innerHTML=h;$(p+'-percent').innerHTML=(s>0?0|100*h/s+.5:0)+'%';$(q).innerHTML=t;$(q+'-percent').innerHTML=(s>0?0|100*t/s+.5:0)+'%'};document.body.onload=()=>{$('flip').onclick=()=>{r=Math.random()>0.5;$('penny').src=`./assets/images/penny-${r?p:q}.jpg`;r?h++:t++;$("status").innerHTML=`You flipped ${r?p:q}!`;b()};$('clear').onclick=()=>{h=t=0;b()}}
