p='heads';q='tails';$=id=>document.getElementById(id);h=t=0;b=()=>{s=h+t;i=s>0?0|100*h/s+.5:0;u=s>0?0|100*t/s+.5:0;$(p).innerHTML=h;$(`${p}-percent`).innerHTML=`${i}%`;$(q).innerHTML=t;$(`${q}-percent`).innerHTML=`${u}%`};document.addEventListener('DOMContentLoaded',()=>{$('flip').addEventListener('click',()=>{r=Math.random()>0.5;$('penny').src=`./assets/images/penny-${r?p:q}.jpg`;r?h++:t++;document.querySelector('.message-container').innerHTML=`You flipped ${r?p:q}!`;b()});$('clear').addEventListener('click',()=>{h=t=0;b()})})
