let h=t=0;let us=()=>{let tt=h+t;let hp=tt>0?((h*100)/tt)|0:0;let tp=tt>0?((t*100)/tt)|0:0;document.getElementById('heads').textContent=h;document.getElementById('heads-percent').textContent=`${hp}%`;document.getElementById('tails').textContent=t;document.getElementById('tails-percent').textContent=`${tp}%`};document.addEventListener('DOMContentLoaded',()=>{document.getElementById('flip').addEventListener('click',()=>{let iH=Math.random()>0.5;document.getElementById('penny').src=`./assets/images/penny-${iH ?'heads':'tails'}.jpg`;if(iH)h++;else t++;document.querySelector('.message-container').textContent=`You rolled ${iH?'Heads':'Tails'}!!`;us()});document.getElementById('clear').addEventListener('click',()=>{h=t=0;us()})})
