h=t=0;b=()=>{s=h+t;i=s>0?((h*100)/s)|0:0;u=s>0?((t*100)/s)|0:0;document.getElementById('heads').innerHTML=h;document.getElementById('heads-percent').innerHTML=`${i}%`;document.getElementById('tails').innerHTML=t;document.getElementById('tails-percent').innerHTML=`${u}%`};document.addEventListener('DOMContentLoaded',()=>{document.getElementById('flip').addEventListener('click',()=>{r=Math.random()>0.5;document.getElementById('penny').src=`./assets/images/penny-${r?'heads':'tails'}.jpg`;r?h++:t++;document.querySelector('.message-container').innerHTML=`You rolled ${r?'Heads':'Tails'}!`;b()});document.getElementById('clear').addEventListener('click',()=>{h=t=0;b()})})
