let h=t=0;let u=()=>{let s=h+t;let i=s>0?((h*100)/s)|0:0;let u=s>0?((t*100)/s)|0:0;document.getElementById('heads').textContent=h;document.getElementById('heads-percent').textContent=`${i}%`;document.getElementById('tails').textContent=t;document.getElementById('tails-percent').textContent=`${u}%`};document.addEventListener('DOMContentLoaded',()=>{document.getElementById('flip').addEventListener('click',()=>{let r=Math.random()>0.5;document.getElementById('penny').src=`./assets/images/penny-${r ?'heads':'tails'}.jpg`;if(r)h++;else t++;document.querySelector('.message-container').textContent=`You rolled ${r?'Heads':'Tails'}!!`;u()});document.getElementById('clear').addEventListener('click',()=>{h=t=0;u()})})
